<template>
<div>
<body background="data:image/jpeg;base64,">
    <h1 class="h1" >ข้อมูลผู้ใช้งาน</h1>
  
  
  
     <b-button variant="outline-primary" @click="loadUser">แสดงข้อมูลผู้ใช้</b-button>
     <br>
    <!-- {{userList}} -->
   <div class="container">
     <div align="center">  

    <b-card 
    bg-variant="" text-variant="" :header="user.id"  v-for="user in userList" :key="user.id"
    img-src=""
    img-alt="Image"
    img-top
    tag="article"
    style="max-width: 50rem;"
    class="card md-3" >
      <b-card-text>
       hssID : {{user.hssID}}
        <br> 
       <h4> ชื่อ: {{user.eventTime}}  </h4>
       <div v-for="i in user.instruments" :key="i">
           {{i.displayName}}

         </div >
        <div v-for="j in user.linkedEvents" :key="j">
          {{j.activityID}}
        </div>
       <a :href="user.link">ลิ้งข้อมูล</a>
      
         </b-card-text>
    </b-card>
    </div> 
    </div>
   </body>
    </div>
</template>

<script>
import axios from"axios"; 
export default {
    data(){
        return{
        userList:null,
         url:"https://api.nasa.gov/DONKI/HSS?startDate=yyyy-MM-dd&endDate=yyyy-MM-dd&api_key=DEMO_KEY"
        };
    },
     methods:{
        loadUser(){
            axios.get(this.url)
            .then( response=>{ this.userList = response.data;       } )
            .catch(err => { console.log(err) } )
        },
     }
};
</script>
 <style>
.h1{
    color: rgb(53, 79, 199);
}
.h2{
   color: rgb(231, 238, 235);
   width: 12;
   font: 1em sans-serif;
}
.card{
    color: rgb(3, 3, 3);
}
</style>